interface TreeConfig {
  getId?: (node: any) => any;
  getChildren?: (node: any) => any[];
  shouldFlattenCallback?: (node: any) => boolean;
}

interface TreeArgs {
  flattened: any[];
}

interface TreeNode {
  id?: any,
  depth?: number,
  parent?: any,
}

export default (
  source: any,
  {
    getId = (node) => node.id,
    getChildren = (node) => node.children,
    shouldFlattenCallback = (node: any) => node.children.length,
  }: TreeConfig
): TreeArgs => {

  if(!getChildren(source)) {
    throw new Error("built-in getChildren returns undefined, please specify a correct getChildren")
  }

  if(!getId(source)) {
    throw new Error("built-in getId returns undefined, please specify a correct getId")
  }

  let depth = 0;

  const toFinalNode = (
    value: any,
    { depth = 0, id = getId(value), parent = null }: TreeNode = {}
  ) => {
    return { id, depth, parent, value };
  };

  const reduce = (getChildren: any, node: any) => {
    depth++;
    return getChildren(node).reduce((accumulator: any, value: any): any[] => {
      accumulator.push(toFinalNode(value, { depth, parent: node }));
      if (shouldFlattenCallback(value)) {
        accumulator = accumulator.concat(reduce(getChildren, value));
      }
      return accumulator;
    }, []);
  };

  const flattened = [
    toFinalNode(source),
    ...reduce(getChildren, source),
  ];

  return { flattened };
};
