interface TreeConfig {
  getChildren?: (node: any) => any[];
  shouldFlattenCallback?: (node: any) => boolean;
}

interface TreeArgs {
  flattened: any[];
}

const reduce = (getChildren: any, shouldFlattenCallback: any) => (reducerFn: any, init: any, node: any) => {
  const acc = reducerFn(init, node);
  if (!shouldFlattenCallback) {
    return acc;
  }
  return getChildren(node).reduce(reduce(reducerFn, null), acc);
}

export default (
  source: any,
  {
    getChildren = (node) => node['children'],
    shouldFlattenCallback,
  }: TreeConfig
): TreeArgs => {

  const flattened = reduce(getChildren, shouldFlattenCallback)(function() {console.log(arguments)}, [], {})

  return { flattened };
};
