interface TreeConfig {
  getChildren?: (node: any) => [any];
  shouldFlattenCallback?: (node: any) => boolean;
}

interface TreeArgs {
  flattened: [any];
}

const flat = (getChildren: (node: any) => [any], callback?: (value: any) => boolean) => (
  accumulator: [any],
  currentValue: any
) => {
  const children = getChildren(currentValue)
  if (!callback && callback(currentValue)) {
    return children.reduce(flat, accumulator);
  }
  return accumulator;
};

export default (
  source: any,
  {
    getChildren = (node) => node['children'],
    shouldFlattenCallback,
  }: TreeConfig
): TreeArgs => {
  const flattened = getChildren(source).reduce(flat(getChildren, shouldFlattenCallback), []);
  return { flattened };
};
