interface TreeConfig {
  getChildren?: (node: any) => any[];
  shouldFlattenCallback?: (node: any) => boolean;
}

interface TreeArgs {
  flattened: any[];
}

export default (
  source: any,
  {
    getChildren = (node) => node.children,
    shouldFlattenCallback = (node: any) => node.children.length,
  }: TreeConfig
): TreeArgs => {
  let depth = 0;

  const reduce = (getChildren: any, node: any) => {
    depth++;
    return getChildren(node).reduce((accumulator: any, value: any): any[] => {
      accumulator.push({ depth, parent: node, value });
      if (shouldFlattenCallback(value)) {
        accumulator = accumulator.concat(reduce(getChildren, value));
      }
      return accumulator;
    }, []);
  };

  const flattened = [
    { depth: 0, parent: null, value: source },
    ...reduce(getChildren, source),
  ];

  return { flattened };
};
